mapper.select:
select

from t_receive_sms where 1=1

	and receive_date = #{receiveDate,jdbcType=TIMESTAMP}


	and mobile_no = #{mobileNo,jdbcType=VARCHAR}


	and sp_gate = #{spGate,jdbcType=VARCHAR}


	and sub_port = #{subPort,jdbcType=VARCHAR}


	and content = #{content,jdbcType=VARCHAR}


	and biz_type = #{bizType,jdbcType=VARCHAR}
